import React, {useEffect, useState} from "react";
import {db} from "../firebaseConfig";
import {toast} from "react-toastify";

export default function Comment({idPost, user}) {
    const [comments, setComments] = useState('')
    const [comment, setComment] = useState([]);
    // console.log("Check id item", idPost);

    const handleComment = () => {
        db.collection("comment").doc(`${idPost}`).collection("postComment").add({
            userRef: db.doc(`users/${user}`),
            comment: comments
        })
    }
    useEffect(() => {
        try {
            return db.collection(`comment/${idPost}/postComment`)
                .onSnapshot( (snap) => {
                    let array = []
                    snap.forEach(async (doc) => {
                        array.push({
                            id: doc.id,
                            comment: doc.data().comment,
                        });
                    })

                    setComment(array);
                    console.log(array)
                })

        } catch (error) {
            toast(error.code, {type: "error"});
        }
    }, [idPost])

    return (
        <div>
            <ul>
                {comment.map((comment) => (
                    <li key={comment.id}>
                        {comment.comment}
                    </li>
                ))}
            </ul>
            {user && (
                <>
                    <input
                        type="text"
                        value={comments}
                        onChange={(e) => {
                            setComments(e.target.value);
                        }}>
                    </input>
                    <button
                        onClick={() =>
                            handleComment()}>
                        Comment
                    </button>
                </>
            )}
        </div>
    )
}
