import React, {useEffect, useState} from "react";
import {db} from "../firebaseConfig";
import {toast} from "react-toastify";
import {onSnapshot} from "firebase/firestore";

export default function Comment({idPost}) {
    const [comments, setComments] = useState('')
    const [comment, setComment] = useState([]);
    // console.log("Check id item", idPost);

    const handleComment = () => {
        db.collection("comment").doc(`${idPost}`).collection("postComment").add({
            comment: comments
        })
    }

    useEffect(()=>{
        try {
            return db.collection(`comment/${idPost}/postComment`)
                .onSnapshot((snap) => {
                let array = []
                snap.forEach((doc)=> {
                    array.push({
                            id: doc.id,
                            comment: doc.data().comment
                        }
                    );
                })
                setComment(array);
            console.log(array)
            })
        } catch (error) {
            toast(error.code, { type: "error" });
        }
    },[idPost])





    return (
        <div>
            {/*tách comment ra khỏi bài Post*/}

            <ul>
                {comment.map((comment) => (
                    <li key={comment.id}>
                        {comment.comment}
                    </li>
                    )
                )}

            </ul>
            <input
                type="text"
                value={comments}
                onChange={(e) => {
                    setComments(e.target.value);
                }}
            ></input>
            <button
                onClick={() => handleComment()}
            >
                Comment
            </button>

        </div>
    )
}
