import React, {useEffect, useState} from "react";
import {db} from "../firebaseConfig";
import {collectionGroup, onSnapshot, query} from "firebase/firestore";
import {snapshotToData} from "react-firebase-hooks/database/dist/database/helpers";


export default function Comment({idPost}) {
    const [comments, setComments] = useState('')
    // console.log("Check id item", idPost);

    const handleComment = () => {
        db.collection("comment").doc(`${idPost}`).collection("postComment").add({
            comment: comments
        })
    }

    useEffect(() => {
        const dbDocComment = query(collectionGroup(db,`comment/${idPost}/postComment`))
        const postComment =  onSnapshot(dbDocComment, async (snapshot) => {
            const post = snapshot.docs.map((doc) =>({
                id: doc.id,
                ...doc.data()
            }))
        })
    })




    return (
        <div>
            {/*tách comment ra khỏi bài Post*/}
            <input
                type="text"
                value={comments}
                onChange={(e) => {
                    setComments(e.target.value);
                }}
            ></input>
            <button
                onClick={() => handleComment()}
            >
                Comment
            </button>

        </div>
    )
}
